class Shop extends React.Component {
  // let state = props.shopPage;
  // console.log(props);
  gamesElements = [
    props.games.map((g) => (
      <Game
        game={g}
        key={g.id}
        name={g.gameName}
        photo={g.gamePhoto}
        developer={g.developer}
        price={g.gamePrice}
        added={g.added}
        addToLibrary={this.addToLibrary}
        removeFromLibrary={this.removeFromLibrary}
      />
    )),
  ];
  render() {
    return (
      <div className={s.shopContent}>
        <ShopHeader
          status={this.status}
          Search={this.Search}
          onFindGame={this.onFindGame}
        />
        <div>
          <div className={s.games}>{this.gamesElements}</div>
        </div>
      </div>
    );
  }
}

export default Shop;
