import React from "react";
import { NavLink } from "react-router-dom";
import Game from "./Game/Game";
import s from "./Shop.module.css";
import { withTranslation } from "react-i18next";
import Card from "./Cards/Card";
import Catalog from "./Catalog/Catalog";
import News from "./News/News";
import FreeGames from "./FreeGames/FreeGames";
import PopularGame from "./PopularGame/PopularGame";
import WishlistGames from "./Wishlist/WishlistGames/WishlistGames";
import Slider from "./Slider/Slider";

class Shop extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      status: "main",
    };
  }

  updateSearch(event) {
    this.setState({ search: event.target.value });
  }

  setStatus(status) {
    this.setState({ status: status });
  }

  render() {
    console.log(this.props);
    let filteredGames = this.props.storePage.games.filter((game) => {
      return (
        game.gameName.toLowerCase().indexOf(this.state.search.toLowerCase()) !==
        -1
      );
    });
    const width = window.innerWidth;
    const { t } = this.props;
    return (
      <div className={s.shopContent}>
        <div className={s.header}>
          <div className={s.links}>
            <div className={s.item}>
              <NavLink
                onClick={() => this.setStatus("main")}
                to="/store/main"
                activeClassName={s.activeLink}
              >
                {t("header.main")}
              </NavLink>
            </div>
            <div className={s.item}>
              <NavLink
                onClick={() => this.setStatus("browse")}
                to="/store/browse"
                activeClassName={s.activeLink}
              >
                {t("header.browse")}
              </NavLink>
            </div>
            <div className={`${s.item} ${s.wishlist}`}>
              <NavLink to="/wishlist" activeClassName={s.activeLink}>
                {t("header.wishlist")}
              </NavLink>
              {this.props.storePage.isWishlistEmpty ? (
                ""
              ) : (
                <div>
                  {this.props.storePage.games.filter((add) => add.inWishlist)
                    .length == 0 ? (
                    ""
                  ) : (
                    <div className={s.count}>
                      {
                        this.props.storePage.games.filter(
                          (add) => add.inWishlist
                        ).length
                      }
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
          <div className={s.search}>
            <input
              placeholder={t("placeholder")}
              type="text"
              value={this.state.search}
              onChange={this.updateSearch.bind(this)}
            />
          </div>
        </div>
        {this.state.status === "main" ? (
          <div>
            <Slider games={this.props.storePage.games} addToLibrary={this.props.addToLibrary} addToWishlist={this.props.addToWishlist}  />
            <div className={s.section}>
              <News games={this.props.storePage.games} />
            </div>
            {this.props.storePage.games.filter((added) => added.inWishlist)
              .length >= 2 ? (
              <div className={s.section}>
                <WishlistGames games={this.props.storePage.games} />
              </div>
            ) : (
              ""
            )}
            <div className={s.section}>
              <FreeGames games={this.props.storePage.games} />
            </div>
            <div className={s.section}>
              <div className={s.text}>Most popular</div>
              <div className={s.filteredGames}>
                {width < 1370 ? this.props.storePage.games
                  .filter(
                    (popular) =>
                      popular.filterBy === "popular" ||
                      popular.filterBy === "top"
                  )
                  .slice(0, 5)
                  .map((game) => {
                    return (
                      <Game
                        game={game}
                        key={game.id}
                        addToLibrary={this.props.addToLibrary}
                        removeFromLibrary={this.props.removeFromLibrary}
                      />
                    );
                  })
                :this.props.storePage.games
                .filter(
                  (popular) =>
                    popular.filterBy === "popular" ||
                    popular.filterBy === "top"
                )
                .slice(0, 5)
                .map((game) => {
                  return (
                    <Game
                      game={game}
                      key={game.id}
                      addToLibrary={this.props.addToLibrary}
                      removeFromLibrary={this.props.removeFromLibrary}
                    />
                  );
                })}
              </div>
            </div>
            <div className={s.section}>
              <div className={s.text}>New and Trending</div>
              <div className={s.cards}>
                <Card
                  games={this.props.storePage.games}
                  addToWishlist={this.props.addToWishlist}
                  removeFromWishlist={this.props.removeFromWishlist}
                  filter={"new"}
                  category={"New Releases"}
                />
                <Card
                  games={this.props.storePage.games}
                  addToWishlist={this.props.addToWishlist}
                  removeFromWishlist={this.props.removeFromWishlist}
                  filter={"top"}
                  category={"Top Sellers"}
                />
                <Card
                  games={this.props.storePage.games}
                  addToWishlist={this.props.addToWishlist}
                  removeFromWishlist={this.props.removeFromWishlist}
                  filter={"soon"}
                  category={"Coming Soon"}
                />
              </div>
            </div>
            <div className={s.section}>
              <PopularGame game={this.props.storePage.games[0]} />
            </div>
            <div className={s.section}>
              <div className={s.text}>Free games</div>
              <div className={s.filteredGames}>
                {this.props.storePage.games
                  .filter((free) => free.gamePrice === "Free")
                  .slice(0, 5)
                  .map((game) => {
                    return (
                      <Game
                        game={game}
                        key={game.id}
                        addToLibrary={this.props.addToLibrary}
                        removeFromLibrary={this.props.removeFromLibrary}
                      />
                    );
                  })}
              </div>
            </div>
            <div className={s.section}>
              <div className={s.text}>Shadow Store Catalog</div>
              <Catalog setStatus={this.setStatus.bind(this)} />
            </div>
          </div>
        ) : (
          <div className={s.games}>
            {filteredGames.map((game) => {
              return (
                <Game
                  game={game}
                  key={game.id}
                  name={game.gameName}
                  photo={game.gamePhoto}
                  developer={game.developer}
                  price={game.gamePrice}
                  added={game.added}
                  gamepage={game.gamepage}
                  addToLibrary={this.props.addToLibrary}
                  removeFromLibrary={this.props.removeFromLibrary}
                />
              );
            })}
          </div>
        )}
      </div>
    );
  }
}

export default withTranslation()(Shop);
