import React from "react";
import LibraryGame from "./LibraryGame";
import s from "./Library.module.css";
import { withTranslation } from "react-i18next";

class Library extends React.Component {
  constructor(props) {
    super(props);
    const { t } = this.props;
    this.state = {
      search: "",
      sorted: false,
      showsort: false,
      filtered: false,
      showfilter: false,
      filterText: t("library.filter.all"),
      sortText: t("library.sort.normal"),
    };
  }
  
  setSort(text) {
    this.setState({ sorted: !this.state.sorted, sortText: [text], showsort: !this.state.showsort });
  }

  setFilter(text) {
    this.setState({ filtered: !this.state.filtered, filterText: [text], showfilter: !this.state.showfilter});
  }

  showFilter(e) {
    this.setState({ showfilter: !this.state.showfilter, filtered: !this.state.filtered });
  }

  showSort(e) {
    this.setState({ showsort: !this.state.showsort, sorted: !this.state.sorted });
  }

  updateSearch(event) {
    this.setState({ search: event.target.value });
  }

  render() {
    let filteredGames = this.props.shopPage.games.filter((game) => {
      return (
        game.gameName.toLowerCase().indexOf(this.state.search.toLowerCase()) !==
        -1
      );
    });
    const { t } = this.props;
    return (
      <div className={s.shopContent}>
        <div className={s.header}>
          <div className={s.sorting}>
            <div className={s.item}>
              <div>{t("library.sort.sort")}:</div>
              <a className={`${s.filter} + ${this.state.sorted ? s.clicked : ""}`} onClick={this.showSort.bind(this)}>
                {this.state.sortText}
              </a>
              <div className={this.state.showsort ? s.show : s.info}>
                <ul className={s.items}>
                  <li>
                    <a
                      className={this.state.sortText == "Normal" ? s.active : ""}
                      onClick={this.setSort.bind(this, t("library.sort.normal"))}
                    >
                      {t("library.sort.normal")}
                    </a>
                  </li>
                  <li>
                    <a
                      className={this.state.sortText == "By alphabet" ? s.active : ""}
                      onClick={this.setSort.bind(this, t("library.sort.byalphabet"))}
                    >
                      {t("library.sort.byalphabet")}
                    </a>
                  </li>
                </ul>
              </div>
            </div>
            <div className={s.item}>
              <div>{t("library.filter.filter")}:</div>
              <a className={`${s.filter} + ${this.state.filtered ? s.clicked : ""}`} onClick={this.showFilter.bind(this)}>
                {this.state.filterText}
              </a>
              <div className={this.state.showfilter ? s.show : s.info}>
                <ul className={s.items}>
                  <li>
                    <a
                      className={this.state.filterText == "All" ? s.active : ""}
                      onClick={this.setFilter.bind(this, t("library.filter.all"))}
                    >
                      {t("library.filter.all")}
                    </a>
                  </li>
                  <li>
                    <a
                      className={this.state.filterText == "Downloaded" ? s.active : ""}
                      onClick={this.setFilter.bind(this, t("library.filter.downloaded"))}
                    >
                      {t("library.filter.downloaded")}
                    </a>
                  </li>
                </ul>
              </div>
            </div>
          </div>
          <div className={s.view}>
            <div className>
              <div></div>
              <div></div>
              <div></div>
              <div></div>
            </div>
          </div>
          <div className={s.search}>
            <input
              placeholder={t("placeholder")}
              type="text"
              value={this.state.search}
              onChange={this.updateSearch.bind(this)}
            />
          </div>
        </div>
        <div>
          <div className={s.games}>
            {filteredGames
              .filter((add) => add.added)
              .map((game) => {
                return (
                  <LibraryGame
                    lib="true"
                    game={game}
                    key={game.id}
                    name={game.gameName}
                    photo={game.gamePhoto}
                    developer={game.developer}
                    price={game.gamePrice}
                    removeFromLibrary={this.props.removeFromLibrary}
                    downloadGame={this.props.downloadGame}
                    deleteGame={this.props.deleteGame}
                  />
                );
              })}
          </div>
        </div>
      </div>
    );
  }
}

export default withTranslation()(Library);
