const SET_LANGUAGE = "SET_LANGUAGE";

let initialState = {
  language: "en",
};

const settingsReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_LANGUAGE:
      return { language: action.language };
    default:
      return state;
  }
};

export const setLanguage = (language) => ({ type: SET_LANGUAGE, language });

export default settingsReducer;
