import React from "react";
import { NavLink } from "react-router-dom";
import { onFindGame } from "../../redux/shop-reducer";
import Game from "./Game/Game";
import s from "./Shop.module.css";
import ShopHeader from "./ShopHeader";

const Shop = (props) => {
  let state = props.shopPage;
  let stat = {
    search: "",
  };
  const onFindGame = (e) => {
    setState({ search: e.target.value })
  } 
  
  console.log(props);
  let gamesElements = [
    state.games.map((g) => (
      <Game
        game={g}
        key={g.id}
        name={g.gameName}
        photo={g.gamePhoto}
        developer={g.developer}
        price={g.gamePrice}
        added={g.added}
        addToLibrary={props.addToLibrary}
        removeFromLibrary={props.removeFromLibrary}
      />
    )),
  ];

  return (
    <div className={s.shopContent}>
      <ShopHeader
        status={state.status}
        Search={props.Search}
        onFindGame={props.onFindGame}
      />
      <div>
        <div className={s.games}>{gamesElements}</div>
      </div>
    </div>
  );
};

export default Shop;
