import React, { Suspense } from "react";
import { Route } from "react-router-dom";
import "./App.css";
import LibraryContainer from "./components/Library/LibraryContainer";
import Navbar from "./components/Navbar/Navbar";
import Game from "./components/Shop/Game/Game";
import StoreContainer from "./components/Shop/StoreContainer";
import WishlistContainer from "./components/Shop/Wishlist/WishlistContainer";
import SettingsContainer from "./components/Settings/SettingsContainer";
import Preloader from "./components/Preloader/Preloader.jsx";
import GamePage from "./components/Shop/Game/GamePage/GamePage";
import { getStore, getView } from "./redux/store-reducer";
import { connect } from "react-redux";
import { getLanguage } from "./redux/language-reducer";

class App extends React.Component {
  componentDidMount() {
    this.props.getStore();
    this.props.getLanguage();
    this.props.getView();
    window.addEventListener("resize", this.updateDimensions);
  }

  updateDimensions = () => {
    this.setState({ width: window.innerWidth });
  };

  componentWillUnmount() {
    window.removeEventListener("resize", this.updateDimensions);
  }
  render() {
    return (
      <div className="app-wrapper">
        <Suspense fallback={"..."} />
        <Navbar />
        {this.props.storePage.isLoading ? (
          <Preloader />
        ) : (
          <div className="app-wrapper-content">
            <Suspense fallback={<Preloader />}>
              <Route path="/store" render={() => <StoreContainer />} />
            </Suspense>
            <Route path="/library" render={() => <LibraryContainer />} />
            <Route path="/settings" render={() => <SettingsContainer />} />
            <Route path="/wishlist" render={() => <WishlistContainer />} />
            <Route
              path="/gamepage"
              render={(props) => <GamePage {...props} />}
            />
            <Route path="/game" render={() => <Game />} />
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  storePage: state.storePage,
});

export const AppContainer = connect(mapStateToProps, { getStore, getLanguage, getView })(App);

export default App;
