import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import s from "../Shop/Game/Game.module.css";
import l from "./Library.module.css";
import { useTranslation } from "react-i18next";

const LibraryGame = ({ addToLibrary, removeFromLibrary, ...props }) => {
  console.log(props);

  const [isShowed, setShow] = useState(false);

  const toggle = () => {
    setShow(!isShowed);
  };

  let history = useHistory();

  const redirect = (e) => {
    history.push("/gamepage", {
      game: props.game,
      currentPlace: t("game.currentPlace.library"),
    });
  };
  
  const { t } = useTranslation();
  return (
    props.view === "card" ?
    <div className={s.game}>
      <a className={s.photo}>
        <img src={props.photo} />
      </a>
      <div className={s.name}>{props.name}</div>
      <div className={s.infoBtns}>
        <div className={s.download}>
          {props.game.isDownloaded == false ? (
            <a href="#" className={s.downloaded} onClick={() => props.downloadGame(props.game.id)}>
              {t("library.libraryGame.download")}
            </a>
          ) : (
            <a href="#" className={s.nonDownloaded}>
              {t("library.libraryGame.start")}
            </a>
          )}
        </div>
        <div className={s.btnMore}>
          <a className={s.btn} onClick={toggle}>
            . . .
          </a>
          <div className={isShowed ? s.show : s.info}>
            <ul className={s.items}>
              <li>
                <a onClick={redirect}>{t("library.libraryGame.visit")}</a>
              </li>
              <li>
                <a
                  onClick={() => {
                    removeFromLibrary(props.game.id);
                  }}
                >
                  {t("library.libraryGame.remove")}
                </a>
              </li>
              <li>
                {props.game.isDownloaded == true ? (
                  <a onClick={() => props.deleteGame(props.game.id)}>{t("library.libraryGame.delete")}</a>
                ) : (
                  ""
                )}
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
    :
    <div className={s.game}>
      <div className={`${s.itemInner} ${s.onHover}`}>
          <div className={s.wrapperLeft}>
            <div className={s.fotoBlock}>
              <img className={s.foto} src={props.foto} />
            </div>
            <div className={s.name}>{props.name}</div>
          </div>
          <div className={s.wrapperRight}>
            {props.price === "Free" ? (
              <div className={s.price}>{t("price.free")}</div>
            ) : (
              <div className={s.price}>{props.price} ₴</div>
            )}
          </div>
        </div>
    </div>
  );
};

export default LibraryGame;
