import React from "react";
import { NavLink, useLocation } from "react-router-dom";
import s from "./GamePage.module.css";

const GamePage = (props) => {
  console.log(props.location);
  const location = useLocation();
  let state = location.state;
  return (
    <div>
      <div className={s.header}>
        <div className={s.back}>
          <span>
            <NavLink className={s.link} to="/shop?view">
              Back to shop
            </NavLink>
          </span>
        </div>
        <div>{state.game.gameName}</div>
      </div>
      <div className={s.foto}>
        <img src={state.game.gamepage.foto} />
      </div>
      <div className={s.mainBlock}>
        <img className={s.mainFoto} src={state.game.gamepage.foto}></img>
        <div className={s.text}>{state.game.gamepage.descr}</div>
        <div className={s.btns}>
          <span className={s.price}>{state.price}</span>
          {state.game.added ? (
            <a className={`${s.btn} ${s.buy}`}>Go to library</a>
          ) : (
            <a
              className={`${s.btn} ${s.buy}`}
              onClick={() => {
                props.addToLibrary(state.game.id);
              }}
              href="#"
            >
              Buy now
            </a>
          )}
          <div className={s.wishlist}>
            <div className={s.wish}>Add to Wishlist</div>
            <a className={`${s.btn} ${s.plus}`}>+</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GamePage;
