import ImageCarousel, { ImageType } from "./Slider";
import { useEffect, useState } from "react";
import s from "./Slider.module.css";

const Slide = (props: object) => {
  games: object;
  const image = [
    "https://cdn2.unrealengine.com/fortnite-battle-royale-1920x1080-864336699.jpg",
    "https://images.unsplash.com/photo-1470341223622-1019832be824?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2288&q=80",
    "https://images.unsplash.com/photo-1448630360428-65456885c650?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2094&q=80",
    "https://images.unsplash.com/photo-1534161308652-fdfcf10f62c4?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2174&q=80",
  ];

  const [images, setImages] = useState<ImageType[]>();

  useEffect(() => {
    setImages(
      Array.from(image.keys()).map((id) => ({
        id,
        url: `${image[id]}`,
      }))
    );
  }, []);

  return (
    <div className={s.App}>
      <ImageCarousel images={images} games={props.games.slice} />
    </div>
  );
};

export default Slide;
