import React from "react";
import Shop from "./Shop";
import { connect } from "react-redux";
import { addToLibrary, getShop, onFindGame, removeFromLibrary, Search } from "../../redux/shop-reducer";

class ShopContainer extends React.Component {
  componentDidMount () {
    this.props.getShop();
  }
    
    render() {
        return <Shop {...this.props} onFindGame={this.onFindGame} search={this.state.search} />
    }
}

const mapStateToProps = (state) => {
  return {
    shopPage: state.shopPage
  };
};

export default connect(mapStateToProps, {Search, addToLibrary, removeFromLibrary, onFindGame, getShop})(ShopContainer);
