let initialState = {
  games: [],
  isWishlistEmpty: undefined,
  isLoading: false,
};

const languageReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_SHOP:
      return { ...state, games: [...action.games] };
    default:
      return state;
  }
};

export default languageReducer;
