            <div className={s.price}>{props.price} â‚´</div>
          )}
        </div>
      </div>
      <div className={s.btnAdd}>
        {props.game.added ? (
          <a
            onClick={() => {
              removeFromLibrary(props.game.id);
            }}
            href="#"
          >
            Remove from library
          </a>
        ) : (
          <a
            onClick={() => {
              props.addToLibrary(props.game.id);
            }}
            href="#"
          >
            Add to library
          </a>
        )}
      </div>
    </div>
  );
};
