import React from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { addToLibrary, addToWishlist } from "../../../../redux/shop-reducer";
import s from "./GamePage.module.css";

class GamePage extends React.Component {
  constructor(props) {
    super(props);
    console.log(props.location);
    this.state = {
      props: this.props.location.state,
      inLibrary: this.props.location.state.game.added,
      inWishlist: this.props.location.state.game.inWishlist,
    };
  }

  addToLibrary() {
    this.setState({ inLibrary: !this.state.inLibrary });
    this.props.addToLibrary(this.state.props.game.id);
  }

  addToWishlist() {
    this.setState({ inWishlist: !this.state.inWishlist });
    this.props.addToWishlist(this.state.props.game.id);
  }

  render() {
    return (
      <div>
        <div className={s.header}>
          <div className={s.back}>
            <span>
              {this.state.props.currentPlace === "shop" ? (
                <NavLink className={s.link} to="/shop/view">
                  Back to {this.state.props.currentPlace}
                </NavLink>
              ) : (
                <NavLink className={s.link} to="/library">
                  Back to {this.state.props.currentPlace}
                </NavLink>
              )}
            </span>
          </div>
          <div>{this.state.props.game.gameName}</div>
          <div className={s.header}>
          <div className={s.links}>
            <div className={s.item}>
              <NavLink
                onClick={() => this.setStatus("view")}
                to="/shop/view"
                activeClassName={s.activeLink}
              >
                View
              </NavLink>
            </div>
            <div className={s.item}>
              <NavLink
                onClick={() => this.setStatus("search")}
                to="/shop/search"
                activeClassName={s.activeLink}
              >
                Search
              </NavLink>
            </div>
            <div className={`${s.item} ${s.wishlist}`}>
              <NavLink to="/wishlist" activeClassName={s.activeLink}>
                Wishlist
              </NavLink>
              {this.props.shopPage.isWishlistEmpty ? (
                ""
              ) : (
                <div className={s.count}>
                  {
                    this.props.shopPage.games.filter((add) => add.inWishlist)
                      .length
                  }
                </div>
              )}
            </div>
          </div>
          {this.state.status === "search" && (
            <div className={s.search}>
              <input
                placeholder="Search"
                type="text"
                value={this.state.search}
                onChange={this.updateSearch.bind(this)}
              />
            </div>
          )}
        </div>
        </div>
        <div className={s.foto}>
          <img src={this.state.props.game.gamepage.foto} />
        </div>
        <div className={s.mainBlock}>
          <img
            className={s.mainFoto}
            src={this.state.props.game.gamepage.foto}
          ></img>
          <div className={s.text}>{this.state.props.game.gamepage.descr}</div>
          <div className={s.btns}>
            {this.state.props.game.gamePrice === "Free" ? (
              <span className={s.price}>{this.state.props.game.gamePrice}</span>
            ) : (
              <span className={`${s.price} ${s.uah}`}>
                UAH {this.state.props.game.gamePrice}
              </span>
            )}
            {this.state.inLibrary ? (
              <NavLink to="/library" className={`${s.btn} ${s.buy}`}>
                Go to library
              </NavLink>
            ) : (
              <a
                className={`${s.btn} ${s.buy}`}
                onClick={this.addToLibrary.bind(this)}
                href="#"
              >
                Buy now
              </a>
            )}
            <div className={s.wishlist}>
              <div className={s.wish}>Add to Wishlist</div>
              <a
                className={`${s.btn} ${s.plus}`}
                onClick={this.addToWishlist.bind(this)}
              >
                +
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {};
};

export default connect(mapStateToProps, { addToLibrary, addToWishlist })(
  GamePage
);
