import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import s from "./Wishlist.module.css";
import h from "../Shop.module.css";
import WishlistItem from "./WishlistItem";
import logoEmpty from "../../../assets/images/logo-empty.png";

const Wishlist = (props) => {
  console.log(props);

  let gameElements = [
    props.shopPage.games
      .filter((add) => add.inWishlist)
      .map((game) => {
        return (
          <WishlistItem
            game={game}
            key={game.id}
            name={game.gameName}
            price={game.gamePrice}
            foto={game.gamepage.foto}
            price={game.gamePrice}
            removeFromWishlist={props.removeFromWishlist}
          />
        );
      }),
  ];

  const addClass = (e) => {
    e.target.classList.toggle(s.clicked);
  };

  const [isFiltered, setFilter] = useState(false);
  const [isShowed, setShow] = useState(false);
  const [isClicked, setClick] = useState(false);
  const [filterText, setFilterText] = useState("On sale");

  const filter = (filter, text) => {
    setFilter(!filter);
    setFilterText(text);
    setShow(!isShowed);
    setClick(!isClicked);
  };

  const doShow = () => {
    setShow(!isShowed);
    setClick(!isClicked);
  };

  return (
    <div>
      <div className={h.header}>
        <div className={h.links}>
          <div className={h.item}>
            <NavLink to="/shop/view" activeClassName={h.activeLink}>
              View
            </NavLink>
          </div>
          <div className={h.item}>
            <NavLink to="/shop/search" activeClassName={h.activeLink}>
              Search
            </NavLink>
          </div>
          <div className={`${h.item} ${h.wishlist}`}>
            <NavLink to="/wishlist" activeClassName={h.activeLink}>
              Wishlist
            </NavLink>
          </div>
        </div>
      </div>
      <div className={s.info}>
        Add items to your wishlist to allow notifications when they become
        available.
      </div>
      <div className={s.spaceBetween}>
        <div className={`${s.sorting} ${s.item}`}>
          <div>Sort by:</div>
          <a
            className={`${s.filter} + ${isClicked ? s.clicked : ""}`}
            onClick={doShow}
          >
            {filterText}
          </a>
          <div className={isShowed ? s.show : s.hidden}>
            <ul className={s.items}>
              <li>
                <a
                  className={isFiltered ? "" : s.active}
                  onClick={() => filter(true, "On sale")}
                >
                  On sale
                </a>
              </li>
              <li>
                <a
                  className={isFiltered ? s.active : ""}
                  onClick={() => filter(false, "Recently added")}
                >
                  Recently added
                </a>
              </li>
            </ul>
          </div>
        </div>
        <div className={s.count}>
          {props.shopPage.games.filter((add) => add.inWishlist).length} game(s)
        </div>
      </div>
      {props.shopPage.isWishlistEmpty ? (
        <div className={s.empty}>
          <img src={logoEmpty} />
          <div className={s.title}>Your wishlist is empty</div>
          <div className={s.text}>
            Games added to your wishlist will appear here
          </div>
          <div className={s.btnBack}>
            <NavLink to="/shop/view">Back to shop</NavLink>
          </div>
        </div>
      ) : (
        <div>{gameElements}</div>
      )}
    </div>
  );
};

export default Wishlist;
