import React from "react";
import s from "./Slider.module.css";
import { NavLink } from "react-router-dom";
import { useTranslation } from "react-i18next";

const Slide = (props) => {
  const { t } = useTranslation();
  return (
    <div className={s.slide}>
      <img src={props.slide.gamepage.foto} />
      <div className={s.content}>
        <div className={s.descr}></div>
        <div className={s.text}></div>
        <div className={s.btns}>
          {props.slide.gamePrice === "Free" ? (
            <span className={s.price}>{t("price.free")}</span>
          ) : (
            <span className={`${s.price} ${s.uah}`}>
              {props.slide.gamePrice} {t("price.currency")}
            </span>
          )}
          {props.slide.added ? (
            <NavLink to="/library" className={`${s.btn} ${s.buy}`}>
              {t("game.in")}
            </NavLink>
          ) : (
            <a
              className={`${s.btn} ${s.buy}`}
              onClick={props.addToLibrary.bind()}
            >
              {props.slide.gamePrice === "Free"
                ? t("game.get")
                : t("game.buy")}
            </a>
          )}
          <div className={s.wishlist}>
            <div
              className={`${s.wish} + ${
                props.languageReducer.language === "ru" ? s.ru : ""
              }`}
            >
              {t("game.add")}
            </div>
            <a
              className={`${s.btn} ${s.plus}`}
              onClick={props.addToLibrary(props.game.id)}
            >
              +
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slide;
