import React from "react";
import { NavLink } from "react-router-dom";
import Game from "./Game/Game";
import s from "./Shop.module.css";
import ShopHeader from "./ShopHeader";

const Shop = (props) => {
  let state = props.shopPage;
  console.log(props);
  let gamesElements = [
    state.games.map((g) => (
      <Game
      key
        name={g.gameName}
        photo={g.gamePhoto}
        developer={g.developer}
        price={g.gamePrice}
        added={g.added}
        addToLibrary={props.addToLibrary}
      />
    )),
  ];

  return (
    <div className={s.shopContent}>
      <ShopHeader status={state.status} Search={props.Search} />
      <div>
        <div className={s.games}>{gamesElements}</div>
      </div>
    </div>
  );
};

export default Shop;
