import React, { useState } from "react";
import { Redirect } from "react-router-dom";
import s from "./Game.module.css";

const Game = ( props ) => {
  console.log(props);

  let addToLibrary = props.addToLibrary;

  const [redirect, set] = useState(false);

  const setRedirect = () => {
    set(true);
  };

  const doRedirect = () => {
    if (redirect) {
      return <Redirect to={{pathname: "/gamepage", state: {...props} }} />;
    }
  };

  // let history = useHistory();

  // const redirect = () => {
  //   history.push("/gamepage", props);
  // };

  return (
    <div className={s.game}>
    {doRedirect()}
      <div onClick={() => setRedirect()}>
        <a className={s.photo}>
          <img src={props.photo} />
        </a>
        <div className={s.name}>{props.name}</div>
        {props.lib === "true" ? (
          ""
        ) : (
          <div className={s.dev}>{props.developer}</div>
        )}
        <div className={s.action}>
          {props.price === "Free" ? (
            <div className={s.price}>{props.price}</div>
          ) : (
            <div className={s.price}>{props.price} â‚´</div>
          )}
        </div>
      </div>
      <div className={s.btnAdd}>
        {props.game.added ? (
          <a
            onClick={() => {
              removeFromLibrary(props.game.id);
            }}
            href="#"
          >
            Remove from library
          </a>
        ) : (
          <a
            onClick={() => {
              addToLibrary(props.game.id);
            }}
            href="/gamepage"
          >
            Add to library
          </a>
        )}
      </div>
    </div>
  );
};

export default Game;
