import React from "react";
import { NavLink } from "react-router-dom";
import Game from "./Game/Game";
import s from "./Shop.module.css";

const Shop = (props) => {
  let state = props.shopPage;
  console.log(props);
  let gamesElements = [
    state.games.map((g) => (
      <Game
        name={g.gameName}
        photo={g.gamePhoto}
        developer={g.developer}
        price={g.gamePrice}
      />
    )),
  ];

  return (
    <div className={s.shopContent}>
      <div className={s.variant}>
        <div className={s.item}>
          <NavLink onClick={() => props.Search("view")} to="/shop/view" activeClassName={s.activeLink}>
            View
          </NavLink>
        </div>
        <div className={s.item}>
          <NavLink onClick={() => props.Search("search")} to="/shop/search" activeClassName={s.activeLink}>
            Search
          </NavLink>
        </div>
      </div>
      <div >
        {state.status === "view" ? (
          <div className={s.games}>{gamesElements}</div>
        ) : (
          <div>
            <div className={s.search}>
              <input placeholder="Search" type="input" />
            </div>
            <div className={s.games}>{gamesElements}</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Shop;
