import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import s from "./Game.module.css";

const Game = ({ addToLibrary, removeFromLibrary, ...props }) => {
  console.log(props);

  let history = useHistory();

  const redirect = () => {
    history.push("/gamepage", { game: props.game, currentPlace});
  };

  return (
    <div className={s.game}>
      <div onClick={redirect}>
        <a className={s.photo}>
          <img src={props.photo} />
        </a>
        <div className={s.name}>{props.name}</div>
        {props.lib === "true" ? (
          ""
        ) : (
          <div className={s.dev}>{props.developer}</div>
        )}
        <div className={s.action}>
          {props.price === "Free" ? (
            <div className={s.price}>{props.price}</div>
          ) : (
            <div className={s.price}>{props.price} â‚´</div>
          )}
        </div>
      </div>
      <div className={s.btnAdd}>
        {props.game.added ? (
          <a
            onClick={() => {
              removeFromLibrary(props.game.id);
            }}
            href="#"
          >
            Remove from library
          </a>
        ) : (
          <a
            onClick={() => {
              addToLibrary(props.game.id);
            }}
            href="#"
          >
            Add to library
          </a>
        )}
      </div>
    </div>
  );
};

export default Game;
