const SliderContent = (props) => {
  console.log(props);
  return (
    <div
      style={
        {transform : `translateX(-${props.translate}px)`},
        {transition : `transform ease-out ${props.transition}s`},
        {height : "500px"},
        {width : `${props.width}px`},
        {display : "flex"}
      }
    >
    </div>
  );
};
export default SliderContent;
