import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import s from "./Wishlist.module.css";
import h from "../Shop.module.css";
import WishlistItem from "./WishlistItem";
import logoEmpty from "../../../assets/images/logo-empty.png";
import { useTranslation } from "react-i18next";

const Wishlist = (props) => {
  console.log(props);

  let gameElements = [
    props.storePage.games
      .filter((add) => add.inWishlist)
      .map((game) => {
        return (
          <WishlistItem
            game={game}
            key={game.id}
            name={game.gameName}
            price={game.gamePrice}
            foto={game.gamepage.foto}
            price={game.gamePrice}
            removeFromWishlist={props.removeFromWishlist}
          />
        );
      }),
  ];

  const addClass = (e) => {
    e.target.classList.toggle(s.clicked);
  };
  const { t } = useTranslation();

  const [isFiltered, setFilter] = useState(false);
  const [isShowed, setShow] = useState(false);
  const [isClicked, setClick] = useState(false);
  const [filterText, setFilterText] = useState(t("wishlist.sort.sale"));

  const filter = (filter, text) => {
    setFilter(!filter);
    setFilterText(text);
    setShow(!isShowed);
    setClick(!isClicked);
  };

  const doShow = () => {
    setShow(!isShowed);
    setClick(!isClicked);
  };
  return (
    <div className={s.wishlist}>
      <div className={h.header}>
        <div className={h.links}>
          <div className={h.item}>
            <NavLink to="/shop/view" activeClassName={h.activeLink}>
              {t("header.view")}
            </NavLink>
          </div>
          <div className={h.item}>
            <NavLink to="/shop/search" activeClassName={h.activeLink}>
              {t("header.search")}
            </NavLink>
          </div>
          <div className={`${h.item} ${h.wishlist}`}>
            <NavLink to="/wishlist" activeClassName={h.activeLink}>
              {t("header.wishlist")}
            </NavLink>
            {props.storePage.isWishlistEmpty ? (
              ""
            ) : (
              <div>
                {props.storePage.games.filter((add) => add.inWishlist).length ==
                0 ? (
                  ""
                ) : (
                  <div className={h.count}>
                    {
                      props.storePage.games.filter((add) => add.inWishlist)
                        .length
                    }
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
      <div className={s.info}>{t("wishlist.info")}</div>
      <div className={s.spaceBetween}>
        <div className={`${s.sorting} ${s.item}`}>
          <div>{t("wishlist.sort.sort")}:</div>
          <a
            className={`${s.filter} + ${isClicked ? s.clicked : ""}`}
            onClick={doShow}
          >
            {filterText}
          </a>
          <div
            className={`${isShowed ? s.show : s.hidden} + ${
              filterText === "Распродажа" ||
              filterText === "Недавно добавленные"
                ? s.ru
                : ""
            }`}
          >
            <ul className={s.items}>
              <li>
                <a
                  className={isFiltered ? "" : s.active}
                  onClick={() => filter(true, t("wishlist.sort.sale"))}
                >
                  {t("wishlist.sort.sale")}
                </a>
              </li>
              <li>
                <a
                  className={isFiltered ? s.active : ""}
                  onClick={() => filter(false, t("wishlist.sort.recently"))}
                >
                  {t("wishlist.sort.recently")}
                </a>
              </li>
            </ul>
          </div>
        </div>
        {props.storePage.isWishlistEmpty ? (
          ""
        ) : (
          <div className={s.count}>
            {props.storePage.games.filter((add) => add.inWishlist).length}{" "}
            game(s)
          </div>
        )}
      </div>
      {props.storePage.isWishlistEmpty ? (
        <div className={s.empty}>
          <img src={logoEmpty} />
          <div className={s.title}>{t("wishlist.empty.empty")}</div>
          <div className={s.text}>{t("wishlist.empty.descr")}</div>
          <div className={s.btnBack}>
            <NavLink to="/shop/view">{t("wishlist.empty.back")}</NavLink>
          </div>
        </div>
      ) : (
        <div>{gameElements}</div>
      )}
    </div>
  );
};

export default Wishlist;
