class Library extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
    };
  }

  updateSearch(event) {
    this.setState({ search: event.target.value });
  }

  render() {
    let filteredGames = this.props.shopPage.games.filter((game) => {
      return (
        game.gameName.toLowerCase().indexOf(this.state.search.toLowerCase()) !==
        -1
      );
    });

    return (
      <div className={s.shopContent}>
        <div className={s.header}>
          <div className={s.sorting}>
            <div className={s.item}>
              <div>Sort:</div>
              <a>Normal</a>
            </div>
            <div className={s.item}>
              <div>Filter:</div>
              <a>All</a>
            </div>
          </div>
          <div className={`${s.search} ${s.lib}`}>
            <input
              placeholder="Search"
              type="text"
              value={this.state.search}
              onChange={this.updateSearch.bind(this)}
            />
          </div>
        </div>
        <div>
          <div className={s.games}>
            {filteredGames
              .filter((add) => add.added)
              .map((game) => {
                return (
                  <LibraryGame
                    lib="true"
                    game={game}
                    key={game.id}
                    name={game.gameName}
                    photo={game.gamePhoto}
                    developer={game.developer}
                    price={game.gamePrice}
                    removeFromLibrary={this.props.removeFromLibrary}
                  />
                );
              })}
          </div>
        </div>
      </div>
    );
  }
}

export default Library;
