import { shopAPI } from "../api/api";
import { updateObjectInArray } from "../utils/object-helpers";

const SET_SHOP = "SET_SHOP";
const ADD_TO_LIBRARY = "ADD_TO_LIBRARY";
const REMOVE_FROM_LIBRARY = "REMOVE_FROM_LIBRARY";
const ADD_TO_WISHLIST = "ADD_TO_WISHLIST";
const REMOVE_FROM_WISHLIST = "REMOVE_FROM_WISHLIST";

let initialState = {
  games: [],
  isWishlistEmpty: true,
};

const shopReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_SHOP:
      return { ...state, games: [...action.games] };
    case ADD_TO_LIBRARY:
      return {
        ...state,
        games: updateObjectInArray(state.games, action.gameId, "id", {
          added: true,
        }),
      };
    case REMOVE_FROM_LIBRARY:
      return {
        ...state,
        isWishlistEmpty: true,
        games: updateObjectInArray(state.games, action.gameId, "id", {
          added: false,
        }),
      };
    case ADD_TO_WISHLIST:
      return {
        ...state,
        isWishlistEmpty: false,
        games: updateObjectInArray(state.games, action.gameId, "id", {
          inWishlist: true,
        }),
      };
    case REMOVE_FROM_WISHLIST:
      return {
        ...state,
        games: updateObjectInArray(state.games, action.gameId, "id", {
          inWishlist: false,
        }),
      };
    default:
      return state;
  }
};

export const setShop = (games) => ({ type: SET_SHOP, games });
export const addToLibrary = (gameId) => ({
  type: ADD_TO_LIBRARY,
  gameId,
});

export const removeFromLibrary = (gameId) => ({
  type: REMOVE_FROM_LIBRARY,
  gameId,
});

export const addToWishlist = (gameId) => ({
  type: ADD_TO_WISHLIST,
  gameId,
});

export const removeFromWishlist = (gameId) => ({
  type: REMOVE_FROM_WISHLIST,
  gameId,
});

export const getShop = () => async (dispatch) => {
  shopAPI.getShop().then((data) => {
    dispatch(setShop(data.games));
