import React, { useState } from "react";
import s from "../Shop/Game/Game.module.css";

const LibraryGame = ({ addToLibrary, removeFromLibrary, game, ...props }) => {
  const info = React.createRef();

  let showInfo = () => {
    info.current.classList.toggle("show");
  };
  console.log(props);

  const [isDownload, doDownload] = useState("false");
  const [isShowed, setShow] = useState("false");

  const download = () => {
    doDownload("true");
  };

  const unDownload = () => {
    doDownload("false");
  };

  const toggle = () => {
    setShow(!isShowed);
  };

  return (
    <div className={s.game}>
      <a className={s.photo}>
        <img src={props.photo} />
      </a>
      <div className={s.name}>{props.name}</div>
      <div className={s.infoBtns}>
        <div className={s.download}>
          {isDownload === "false" ? (
            <a href="#" className={s.downloaded} onClick={download}>
              Download
            </a>
          ) : (
            <a href="#" className={s.nonDownloaded} onClick={unDownload}>
              Start
            </a>
          )}
        </div>
        <div className={s.btnMore} onClick={() => showInfo}>
          <a>. . .</a>
          <div className={s.info} ref={info}>
            Hello
          </div>
        </div>
      </div>

      <div className={s.btnAdd}>
        {game.added ? (
          <a
            onClick={() => {
              removeFromLibrary(game.id);
            }}
            href="#"
          >
            Remove from library
          </a>
        ) : (
          <a
            onClick={() => {
              addToLibrary(game.id);
            }}
            href="#"
          >
            Add to library
          </a>
        )}
      </div>
    </div>
  );
};

export default LibraryGame;
