const SET_LANGUAGE = "SET_LANGUAGE";

let initialState = {
  language: "en",
};

const shopReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_LANGUAGE:
      return { language: action.language };
    default:
      return state;
  }
};

export const setLanguage = (language) => ({ type: SET_SHOP, games });

export default shopReducer;
