import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import i18n from "../../i18n";
import s from "./Settings.module.css";

const Settings = (props) => {
  console.log(props);

  const sel = React.createRef();
  const changeLng = React.createRef();

  const changeLanguage = () => {
    return () => {
      i18n.changeLanguage(sel.current.value);
      console.log(sel.current.value);
      changeLng.classList.toggle(s.show);
      var node = ReactDOM.findDOMNode(this.refs.btn);
node.classList.toggle('btn-menu-open');
    };
  };

  return (
    <div className={s.settings}>
      <NavLink className={s.header} to="/shop">
        <div className={s.back}>
          <span></span>
        </div>
        <div className={s.text}>Settings</div>
      </NavLink>
      <div className={s.language}>
        <div className={s.lText}>Language</div>
        <form>
          <select
            className={s.select}
            ref={sel}
            name="language"
            onChange={changeLanguage()}
          >
            {props.settingsPage.language === "en" ? (
              <option selected value="en">
                English
              </option>
            ) : (
              <option value="en">English</option>
            )}
            {props.settingsPage.language === "en" ? (
              <option value="ru">Russia</option>
            ) : (
              <option selected value="ru">
                Russia
              </option>
            )}
          </select>
        </form>
        <div className={s.changeLng} ref={changeLng}>
          <a>Cancel</a>
          <a>Confirm</a>
        </div>
        <button onClick={() => props.setLanguage(sel.current.value)}>
          Confirm
        </button>
      </div>
    </div>
  );
};

export default Settings;
