class Shop extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      status: "view",
    };
  }

  updateSearch(event) {
    this.setState({ search: event.target.value });
  }

  setStatus(status) {
    this.setState({ status: status });
  }

  // gamesElements = [
  //   this.props.shopPage.games.map((g) => (
  //     <Game
  //       game={g}
  //       key={g.id}
  //       name={g.gameName}
  //       photo={g.gamePhoto}
  //       developer={g.developer}
  //       price={g.gamePrice}
  //       added={g.added}
  //       gamepage={g.gamepage}
  //       addToLibrary={this.props.addToLibrary}
  //       removeFromLibrary={this.props.removeFromLibrary}
  //     />
  //   )),
  // ];
  render() {
    console.log(this.props.shopPage.games);
    let filteredGames = this.props.shopPage.games.filter((game) => {
      return (
        game.gameName.toLowerCase().indexOf(this.state.search.toLowerCase()) !==
        -1
      );
    });

    return (
      <div className={s.shopContent}>
        <div className={s.header}>
          <div className={s.links}>
            <div className={s.item}>
              <NavLink
                onClick={() => this.setStatus("view")}
                to="/shop/view"
                activeClassName={s.activeLink}
              >
                View
              </NavLink>
            </div>
            <div className={s.item}>
              <NavLink
                onClick={() => this.setStatus("search")}
                to="/shop/search"
                activeClassName={s.activeLink}
              >
                Search
              </NavLink>
            </div>
          </div>
          {this.state.status === "search" && (
            <div className={s.search}>
              <input
                placeholder="Search"
                type="text"
                value={this.state.search}
                onChange={this.updateSearch.bind(this)}
              />
            </div>
          )}
        </div>
        <div className={s.games}>
          {filteredGames.map((game) => {
            return (
              <Game
                game={game}
                key={game.id}
                name={game.gameName}
                photo={game.gamePhoto}
                developer={game.developer}
                price={game.gamePrice}
                added={game.added}
                gamepage={game.gamepage}
                addToLibrary={this.props.addToLibrary}
                removeFromLibrary={this.props.removeFromLibrary}
              />
            );
          })}
        </div>
      </div>
    );
  }
}

export default Shop;
