const Shop = (props) => {
  let state = props.shopPage;
  console.log(props);
  let gamesElements = [
    state.games.map((g) => (
      <Game
        game={g}
        key={g.id}
        name={g.gameName}
        photo={g.gamePhoto}
        developer={g.developer}
        price={g.gamePrice}
        added={g.added}
        addToLibrary={props.addToLibrary}
        removeFromLibrary={props.removeFromLibrary}
      />
    )),
  ];

  return (
    <div className={s.shopContent}>
      <ShopHeader
        status={state.status}
        Search={props.Search}
      />
      <div>
        <div className={s.games}>{gamesElements}</div>
      </div>
    </div>
  );
};

export default Shop;
