import React from "react";
import Shop from "./Shop";
import { connect } from "react-redux";
import { addToLibrary, getShop, removeFromLibrary } from "../../redux/shop-reducer";
import Preloader from "../Preloader/Preloader";

class ShopContainer extends React.Component {
  componentDidMount () {
    this.props.getShop();
  }
    
    render() {
        return <>
          {this.props = undefined ?  <Preloader /> : null}
        <Shop {...this.props} />
        </>
    }
}

const mapStateToProps = (state) => {
  return {
    shopPage: state.shopPage
  };
};

export default connect(mapStateToProps, {addToLibrary, removeFromLibrary, getShop})(ShopContainer);
