    //     </div>
    //   </div>
    //   <div >
    //     {state.status === "view" ? (
    //       <div className={s.games}>{gamesElements}</div>
    //     ) : (
    //       <div>
    //         <div className={s.search}>
    //           <input placeholder="Search" type="input" />
    //         </div>
    //         <div className={s.games}>{gamesElements}</div>
    //       </div>
    //     )}
    //   </div>
    // </div>
    <div className={s.shopContent}>
      <ShopHeader status={state.status} search />
      <div >
        {state.status === "view" ? (
          <div className={s.games}>{gamesElements}</div>
        ) : (
          <div>
            <div className={s.search}>
              <input placeholder="Search" type="input" />
            </div>
            <div className={s.games}>{gamesElements}</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Shop;
