import React from "react";
import { NavLink } from "react-router-dom";
import s from "./GamePage.module.css";

const GamePage = (props) => {
  console.log(props.location);
  let state = props.location.state;
  let addToLibrary = props.location.addToLibrary;
  return (
    <div>
      <div className={s.header}>
        <div className={s.back}>
          <span>
            <NavLink className={s.link} to="/shop?view">
              Back to shop
            </NavLink>
          </span>
        </div>
        <div>{state.name}</div>
      </div>
      <div className={s.foto}>
        <img src={state.gamepage.foto} />
      </div>
      <div className={s.mainBlock}>
        <img className={s.mainFoto} src={state.gamepage.foto}></img>
        <div className={s.text}>{state.gamepage.descr}</div>
        <div className={s.btns}>
          <span className={s.price}>{state.price}</span>
          {state.game.added ? (
          <a
            onClick={() => {
              removeFromLibrary(state.game.id);
            }}
            href="#"
          >
            Remove from library
          </a>
        ) : (
          <a
            onClick={() => {
              addToLibrary(state.game.id);
            }}
            href="#"
          >
            Add to library
          </a>
        )}
          <a className={`${s.btn} ${s.buy}`}>Buy now</a>
          <div className={s.wishlist}>
            <div className={s.wish}>Add to Wishlist</div>
            <a className={`${s.btn} ${s.plus}`}>+</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GamePage;
