import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import s from "./Game.module.css";

const Game = ({ addToLibrary, removeFromLibrary, ...props }) => {
  console.log(props);

  let history = useHistory();

  const redirect = () => {
    history.push("/gamepage", { game: props.game, currentPlace: "shop"});
  };

  return (
    <div className={s.game} onClick={redirect}>
      <div >
        <a className={s.photo}>
          <img src={props.photo} />
        </a>
        <div className={s.name}>{props.name}</div>
        {props.lib === "true" ? (
          ""
        ) : (
          <div className={s.dev}>{props.developer}</div>
        )}
        <div className={s.action}>
          {props.price === "Free" ? (
            <div className={s.price}>{props.price}</div>
          ) : (
            <div className={s.price}>{props.price} â‚´</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Game;
