import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import s from "./Wishlist.module.css";
import h from "../Shop.module.css";
import WishlistItem from "./WishlistItem";

const Wishlist = (props) => {
  console.log(props);

  let gameElements = [
    props.shopPage.games
      .filter((add) => add.inWishlist)
      .map((game) => {
        return (
          <WishlistItem
            game={game}
            key={game.id}
            name={game.gameName}
            price={game.gamePrice}
            foto={game.gamepage.foto}
            price={game.gamePrice}
            removeFromWishlist={props.removeFromWishlist}
          />
        );
      }),
  ];

  return (
    <div>
      <div className={h.header}>
        <div className={h.links}>
          <div className={h.item}>
            <NavLink to="/shop/view" activeClassName={h.activeLink}>
              View
            </NavLink>
          </div>
          <div className={h.item}>
            <NavLink to="/shop/search" activeClassName={h.activeLink}>
              Search
            </NavLink>
          </div>
          <div className={`${h.item} ${h.wishlist}`}>
            <NavLink to="/wishlist" activeClassName={h.activeLink}>
              Wishlist
            </NavLink>
          </div>
        </div>
      </div>
      {props.shopPage.isWishlistEmpty ? (
        <div className={s.empty}>
          <div className={s.title}>Your wishlist is empty</div>
          <div className={s.text}>Here will be games, which are added to your wishlist</div>
          <div className={s.btn}>
            <a>Back to shop</a>
          </div>
        </div>
      ) : (
        <div>{gameElements}</div>
      )}
    </div>
  );
};

export default Wishlist;
