import React from "react";
import { NavLink } from "react-router-dom";
import Game from "./Game/Game";
import s from "./Shop.module.css";

const Shop = (props) => {
  let state = props.shopPage;

  let gamesElements = [
    state.games.map((g) => (
      <Game
        name={g.gameName}
        photo={g.gamePhoto}
        developer={g.developer}
        price={g.gamePrice}
      />
    )),
  ];

  return (
    <div className={s.shopContent}>
      <div className={s.variant}>
        <div className={s.item}>
          <NavLink to="/shop/view" activeClassName={s.activeLink}>
            View
          </NavLink>
        </div>
        <div className={s.item}>
          <NavLink to="/shop/search" activeClassName={s.activeLink}>
            Search
          </NavLink>
        </div>
      </div>
      
      <div>{gamesElements}</div>
    </div>
  );
};

export default Shop;
