import React from "react";
import FreeGame from "./FreeGame";
import s from "./FreeGames.module.css";
import giftIcon from "../../../assets/images/giftbox.svg";

const FreeGames = (props) => {
  var currentDate = new Date();
  var month = currentDate.toLocaleString("en", { month: "short" });
  var day = currentDate.getDate();
  return (
    <div className={s.freeGames}>
      <div className={s.header}>
        <div className={s.logo}>
          <img src={giftIcon} />
          <div className={s.title}>Free Games</div>
        </div>
        <div className={s.btn}>View more</div>
      </div>
      <div className={s.games}>
        {props.games
          .filter((free) => free.temporarilyFree)
          .map((game) => {
            return <FreeGame game={game} key={game.id} />;
          })}
      </div>
    </div>
  );
};

export default FreeGames;
