import React, { useState } from "react";
import s from "./Wishlist.module.css";
import success from "../../../assets/images/icon success.svg";

const WishlistItem = (props = undefined) => {
  console.log(props);

  const [isConfirmed, setConfirm] = useState(false);

  const removeFromWishlist = (e) => {
    setConfirm(true);
    props.removeFromWishlist(props.game.id);
    e.target.classlist.add("hidden");
  };
  return (
    <div className={s.item}>
      {isConfirmed === false ? (
        <div className={s.wrapperLeft}>
          <div className={s.fotoBlock}>
            <img className={s.foto} src={props.foto} />
          </div>
          <div className={s.name}>{props.name}</div>
        </div>
      ) : (
        <div className={`${s.wrapperLeft} ${s.hidden}`}>
          <div className={`${s.fotoBlock} ${s.hidden}`}>
            <img className={`${s.foto} ${s.hidden}`} src={props.foto} />
          </div>
          <div className={`${s.name} ${s.hidden}`}>{props.name}</div>
        </div>
      )}
      <div className={s.wrapperRight}>
        <div className={s.price}>{props.price}</div>
        <div className={s.btn}>
          <a onClick={removeFromWishlist}>
            <img src={success} width="15px" />
          </a>
        </div>
      </div>
    </div>
  );
};

export default WishlistItem;
