import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import s from "../Shop/Game/Game.module.css";

const LibraryGame = ({ addToLibrary, removeFromLibrary, ...props }) => {
  console.log(props);

  const [isDownload, doDownload] = useState("false");
  const [isShowed, setShow] = useState(false);

  const download = () => {
    doDownload("true");
    ret
  };

  const unDownload = () => {
    doDownload("false");
  };

  const toggle = () => {
    setShow(!isShowed);
  };

  let history = useHistory();

  const redirect = (e) => {
    history.push("/gamepage", { game: props.game, currentPlace: "library" });
  };

  return (
    <div className={s.game}>
      <a className={s.photo}>
        <img src={props.photo} />
      </a>
      <div className={s.name}>{props.name}</div>
      <div className={s.infoBtns}>
        <div className={s.download}>
          {isDownload === "false" ? (
            <a href="#" className={s.downloaded} onClick={download}>
              Download
            </a>
          ) : (
            <a href="#" className={s.nonDownloaded}>
              Start
            </a>
          )}
        </div>
        <div className={s.btnMore}>
          <a className={s.btn} onClick={toggle}>
            . . .
          </a>
          <div className={isShowed ? s.show : s.info}>
            <ul className={s.items}>
              <li>
                <a onClick={redirect}>Visit game page</a>
              </li>
              <li>
                <a
                  onClick={() => {
                    removeFromLibrary(props.game.id);
                  }}
                >
                  Remove from library
                </a>
              </li>
              <li>
                {isDownload === "true" ? (
                  <a onClick={unDownload}>Delete</a>
                ) : (
                  ""
                )}
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LibraryGame;
