import React, { useState } from "react";
import { Redirect, useHistory } from "react-router-dom";
import s from "./Game.module.css";
import GamePage from "./GamePage/GamePage";

const Game = ({ addToLibrary, removeFromLibrary, game, name, ...props }) => {
  console.log(props);

  const [redirect, doRed] = useState(false);

  const setRedirect = () => {
    doRed(true);
  };

  const doRedirect = () => {
    if (redirect) {
      return <Redirect to="/gamepage" state={props.name} />;
    }
  };

  // let history = useHistory();

  // const redirect = () => {
  //   history.push("/gamepage", props);
  // };

  return (
    <div className={s.game}>
    {doRedirect()}
      <div onClick={() => setRedirect()}>
        <a className={s.photo}>
          <img src={props.photo} />
        </a>
        <div className={s.name}>{props.name}</div>
        {props.lib === "true" ? (
          ""
        ) : (
          <div className={s.dev}>{props.developer}</div>
        )}
        <div className={s.action}>
          {props.price === "Free" ? (
            <div className={s.price}>{props.price}</div>
          ) : (
            <div className={s.price}>{props.price} â‚´</div>
          )}
        </div>
      </div>
      <div className={s.btnAdd}>
        {game.added ? (
          <a
            onClick={() => {
              removeFromLibrary(game.id);
            }}
            href="#"
          >
            Remove from library
          </a>
        ) : (
          <a
            onClick={() => {
              addToLibrary(game.id);
            }}
            href="#"
          >
            Add to library
          </a>
        )}
      </div>
    </div>
  );
};

export default Game;
