import React, { useState, useEffect } from "react";
import s from "./Slider.module.css";

const Slider = (props) => {
  // let baseId = 1;

  let [baseId, setId] = useState(1);
  function showSlide(slideId) {
    setId(slideId);
  }

  
  useEffect(() => {
    function showNextSlide() {
      setId((baseId + 1));
    }
    if (baseId > 5) {
      setId(1);
      setTimeout(showNextSlide, 2000);
    } else {
      setTimeout(showNextSlide, 2000);
    }
  });
  // setTimeout(showNextSlide, 5000);

  console.log(baseId);
  return (
    <div className={s.slider}>
      <div className={s.slides}>
        {props.games
          .slice(0, 5)
          .filter((game) => game.id === baseId)
          .map((slide) => {
            return (
              <div className={s.slide} key={slide.id}>
                <img src={slide.gamepage.foto} />
              </div>
            );
          })}
      </div>
      <div className={s.thumbnails}>
        {props.games.slice(0, 5).map((slide) => {
          return (
            <div
              className={`${s.thumbnail} ${baseId == slide.id ? s.active : ""}`}
              key={slide.id}
              onClick={() => showSlide(slide.id)}
            >
              <img className={s.foto} src={slide.gamePhoto} />
              <div className={s.name}>{slide.gameName}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Slider;
