import { shopAPI } from "../api/api";

const DO_SEARCH = "DO_SEARCH";
const SET_SHOP = "SET_SHOP"

let initialState = {
  games: [
    {
      gamePhoto:
        "https://upload.wikimedia.org/wikipedia/uk/3/35/Fortnite_poster.png",
      gameName: "Fortnite",
      developer: "Epic Games Store",
      gamePrice: "Free",
    },
  ],
  status: "view",
};

const shopReducer = (state = initialState, action) => {
  switch (action.type) {
    case DO_SEARCH:
      return { ...state, status: action.status };
    default:
      return state;
  }
};

export const setShop = (shop) => ({type: SET_SHOP, shop})
export const Search = (status) => ({ type: DO_SEARCH, status });

export const getShop = () => {
  return (dispatch) => {
    shopAPI.getShop().then(data => {
      dispatch(setShop(data.games));
    });
  }
}

export default shopReducer;
