import React from "react";
import s from "../Shop/Game/Game.module.css";

const LibraryGame = ({ addToLibrary, removeFromLibrary, game, ...props }) => {
  console.log(props);

  return (
    <div className={s.game}>
      <a className={s.photo}>
        <img src={props.photo} />
      </a>
      <div className={s.name}>{props.name}</div>
      : <div className={s.dev}>{props.developer}</div>
    }
      <div className={s.action}>
        <div className={s.btn}>
          {game.added ? (
            <a
              onClick={() => {
                removeFromLibrary(game.id);
              }}
              href="#"
            >
              Remove from library
            </a>
          ) : (
            <a
              onClick={() => {
                addToLibrary(game.id);
              }}
              href="#"
            >
              Add to library
            </a>
          )}
        </div>
      </div>
    </div>
  );
};

export default LibraryGame;
