const DO_SEARCH = "DO_SEARCH";

let initialState = {
  games: [
    {
      gamePhoto:
        "https://upload.wikimedia.org/wikipedia/uk/3/35/Fortnite_poster.png",
      gameName: "Fortnite",
      developer: "Epic Games Store",
      gamePrice: "Free",
    },
  ],
  status: undefined,
};

const shopReducer = (state = initialState, action) => {
  switch (action.type) {
    case DO_SEARCH:
      return { ...state, status: action.status };
    default:
      return state;
  }
};

export const Search = (status) => ({ type: DO_SEARCH, status });

export default shopReducer;
