class Shop extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
    };
  }
  updateSearch(event) {
    this.setState({ search: event.target.value });
  }

  gamesElements = [
    this.props.shopPage.games.map((g) => (
      <Game
        game={g}
        key={g.id}
        name={g.gameName}
        photo={g.gamePhoto}
        developer={g.developer}
        price={g.gamePrice}
        added={g.added}
        addToLibrary={this.props.addToLibrary}
        removeFromLibrary={this.props.removeFromLibrary}
      />
    )),
  ];
  render() {
    let filteredGames = this.props.games.filter(
      (game) => {
        return game.name.index
      }
    );
    return (
      <div className={s.shopContent}>
        <ShopHeader
          search={this.state.search}
          updateSearch={this.state.updateSearch}
          status={this.props.shopPage.status}
          Search={this.props.Search}
        />
        <div>
          <div className={s.games}>{this.gamesElements}</div>
        </div>
      </div>
    );
  }
}

export default Shop;
