let initialState = {
  games: [{
    gamePhoto: "https://upload.wikimedia.org/wikipedia/uk/3/35/Fortnite_poster.png",
    gameName: "Fortnite",
    developer: "Epic Games Store",
    gamePrice: "Free"
  }],
  status: "view"
};

const shopReducer = (state = initialState, action) => {
  switch(action.type) {
    case DO_SEARCH: 
    return {status: action.status};
  }
  return state;
};

export const Search = (status) => ({type: DO_SEARCH, status});

export default shopReducer;
