import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import s from "./Game.module.css";
import { useTranslation } from "react-i18next";

const Game = ({ addToLibrary, removeFromLibrary, ...props }) => {
  console.log(props);

  let history = useHistory();

  const redirect = () => {
    history.push("/gamepage", { game: props.game, currentPlace: t("game.currentPlace.store")});
  };

  const { t } = useTranslation();
  return (
    <div className={`${s.game} ${props.class ? s[props.class] : ""}`} onClick={redirect}>
        <div className={s.photo}>
          <img src={props.game.gamePhoto} />
        </div>
        <div className={s.name}>{props.game.gameName}</div>
        {props.lib === "true" ? (
          ""
        ) : (
          <div className={s.dev}>{props.game.developer}</div>
        )}
        <div className={s.action}>
          {props.game.gamePrice === "Free" || props.game.gamePrice === "Soon" ? (
            <div className={s.price}>{t(`price.${props.game.gamePrice.toLowerCase()}`)}</div>
          ) : (
            <div className={s.price}>{props.game.gamePrice} {t("price.currency")}</div>
          )}
        </div>
    </div>
  );
};

export default Game;
